import { API_KEY, API_URL } from "../config";

// action types for home

export const GET_POPULAR_MOVIES = 'GET_POPULAR_MOVIES';
export const SEARCH_MOVIES = 'SEARCH_MOVIES';
export const LOAD_MORE_MOVIES = 'LOAD_MORE_MOVIES';
export const CLEAR_MOVIES = 'CLEAR_MOVIES';

// action for both

export const SHOW_LOADING_SPINNER = 'SHOW_LOADING_SPINNER';

// action creators for both

export function showLoadingSpinner(params) {
    return {
        type: SHOW_LOADING_SPINNER,
        payload: null
    }    

}

// action creators for home 

export function getPopularMovies() {
    const endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
    const request = fetch(endpoint)
    .then(result => result.json())
    .then(result => {
        return result;
    })
    .catch(error => console.error('Error:', error))

    return {
        type: GET_POPULAR_MOVIES,
        payload: request
    }
}

export function searchMovies(searchTerm) {
    let endpoint;
    if (!searchTerm) {
        endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
    } else {
        endpoint = `${API_URL}search/movie?api_key=${API_KEY}&language=en-US&query=${searchTerm}`;
    }

    const request = fetch(endpoint)
    .then(result => result.json())
    .then(result => {
        return {...result, searchTerm};
    })
    .catch(error => console.error('Error:', error))

    return {
        type: SEARCH_MOVIES,
        payload: request
    }
}

export function loadMoreMovies(searchTerm, currentPage) {
    let endpoint;

    if (searchTerm === '') {
        endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=${currentPage + 1}`;
    } else {
        endpoint = `${API_URL}search/movie?api_key=${API_KEY}&language=en-US&query=${searchTerm}&page=${currentPage + 1}`;
    }
    const request = fetch(endpoint)
    .then(result => result.json())
    .then(result => {
        return result;
    })
    .catch(error => console.error('Error:', error))

    return {
        type: LOAD_MORE_MOVIES,
        payload: request
    }    
}

export function clearMovies() {
    return {
        type: CLEAR_MOVIES,
        payload: null
    }    

}
