import {
    GET_POPULAR_PERSISTED_STATE,
    SHOW_LOADING_SPINNER,
    CLEAR_MOVIES,
    SEARCH_MOVIES,
    SET_POPULAR_MOVIES,
    LOAD_MORE_MOVIES
} from '../actions'

const defaultState = {
    movies: [],
    heroImage: null,
    loading: false,
    currentPage: 0,
    totalPages: 0,
    searchTerm: ''
}


export default function(state = defaultState, action) {
    switch(action.type) {

        case SET_POPULAR_PERSISTED_STATE:
            return {
                ...state,
                ...action.payload
            }

        case GET_POPULAR_MOVIES:
            return {
                ...state,
                movies: action.payload.results,
                heroImage: state.heroImage || action.payload.results[0],
                loading: false,
                currentPage: action.payload.page,
                totalPages: action.payload.total_pages,
                searchTerm: ""
            }
        case LOAD_MORE_MOVIES:
            return {
                ...state,
                movies: [...state.movies, ...action.payload.results],
                loading: false,
                currentPage: action.payload.page,
                totalPages: action.payload.total_pages
            }
        case SEARCH_MOVIES: 
            return {
                ...state,
                movies: action.payload.results,
                loading: false,
                currentPage: action.payload.page,
                totalPages: action.payload.total_pages,
                searchTerm: action.payload.searchTerm
            }
        case CLEAR_MOVIES:
            return {
                ...state,
                movies: []
            }
        case SHOW_LOADING_SPINNER: 
            return {
                ...state,
                loading: true
            }
        default:
            return state;
    }
}
